class Estudiante(val nombre: String, val apellido: String, val mail: String) {    val aptitudesDeseada: MutableList<String> = mutableListOf()    val cursosRealizados: MutableList<CursoPosta> = mutableListOf()    fun agregarAptitudesDeseadas(aptitud: String) {        aptitudesDeseada.add(aptitud)    }    fun agregarCurso(curso: Curso) {        cursosRealizados.add(curso as CursoPosta)    }    fun quitarAptitudesDeseadas(aptitud: String) {        aptitudesDeseada.remove(aptitud)    }    fun tieneAptitudEspecifica(aptitud: String): Boolean {        return cursosRealizados.any { it.otorgaAptitud(aptitud) }    }    fun leInteresaCurso(curso: Curso): Boolean {        return aptitudesDeseada.any { curso.otorgaAptitud(it) }    }}